const loader=(a,s)=>{$(s).on("click",o=>{o.preventDefault(),o.stopPropagation();const t=o.target;if($(t).closest(".btn.btn-success").length>0){const o=$("<span></span>").addClass(a),t=$("<div></div>").addClass("wrapperLoader").append(o);$("body").append(t),$(s).off("click")}})};loader("loader",".auction-list");